import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/AgeEventsPage.css';

function AgeEventsPage({ timelineData }) {
  const { age } = useParams();
  const navigate = useNavigate();
  const ageNumber = parseInt(age);

  // 获取指定年龄的所有事件
  const ageEvents = timelineData.flatMap(person => 
    person.experiences
      .filter(exp => exp.year - person.birthYear === ageNumber)
      .map(exp => ({
        ...exp,
        personId: person.id,
        personName: person.name
      }))
  );

  return (
    <div className="age-events-container">
      <div className="back-button" onClick={() => navigate(-1)}>
        返回上页
      </div>
      <div className="age-header">
        <h1>{ageNumber}<span className="age-label">岁</span></h1>
        <p className="age-subtitle">在这个年龄，他们都在做什么</p>
      </div>
      <div className="events-list">
        {ageEvents.map((event, index) => (
          <div key={index} className="event-item" onClick={() => navigate(`/timeline/${event.personId}`)}>
            <div className="event-person">
              {event.personName}
              <span className="event-year">· {event.year}年</span>
            </div>
            <div className="event-content">{event.description}</div>
          </div>
        ))}
        {ageEvents.length === 0 && (
          <div className="no-events">
            这个年龄暂无记录
          </div>
        )}
      </div>
    </div>
  );
}

export default AgeEventsPage;
