import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/YearEventsPage.css';

// 将时间轴数据转换为年份索引
const createYearIndex = (timelineData) => {
  const yearEvents = {};
  Object.entries(timelineData).forEach(([personId, events]) => {
    events.forEach(event => {
      const year = event.year.replace(/[年-].+$/, ''); // 处理类似"1989-1993年"的情况，只取第一个年份
      if (!yearEvents[year]) {
        yearEvents[year] = [];
      }
      yearEvents[year].push({
        personId,
        ...event
      });
    });
  });
  return yearEvents;
};

function YearEventsPage({ timelineData }) {
  const { year } = useParams();
  const navigate = useNavigate();
  const yearEvents = createYearIndex(timelineData);
  const events = yearEvents[year] || [];

  const people = {
    "1": "比尔·盖茨",
    "2": "马云",
    "3": "乔布斯",
    "4": "任正非",
    "5": "柳传志",
    "6": "雷军",
    "7": "李嘉诚",
    "8": "张朝阳",
    "9": "马化腾",
    "10": "王兴"
  };

  return (
    <div className="year-events-container">
      <div className="back-button" onClick={() => navigate(-1)}>
        返回上页
      </div>
      <div className="year-header">
        <h1>{year}<span className="year-label">年</span></h1>
        <p className="year-subtitle">这一年，他们都在做什么</p>
      </div>
      <div className="events-list">
        {events.map((event, index) => (
          <div key={index} className="event-item" onClick={() => navigate(`/timeline/${event.personId}`)}>
            <div className="event-person">{people[event.personId]}</div>
            <div className="event-content">{event.content}</div>
          </div>
        ))}
        {events.length === 0 && (
          <div className="no-events">
            这一年暂无记录
          </div>
        )}
      </div>
    </div>
  );
}

export default YearEventsPage;
