import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/TimelinePage.css';
import { timelineData } from '../data/timelineData';

function TimelinePage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const events = timelineData[id] || [];

  const handleYearClick = (year) => {
    // 处理类似"1989-1993年"的情况，只取第一个年份
    const cleanYear = year.replace(/[年-].+$/, '');
    navigate(`/year/${cleanYear}`);
  };

  return (
    <div className="timeline-container">
      <div className="back-button" onClick={() => navigate('/')}>
        返回首页
      </div>
      <div className="timeline">
        {events.map((event, index) => (
          <div key={index} className="timeline-item">
            <div className="timeline-year-container">
              <div className="timeline-year" onClick={() => handleYearClick(event.year)}>
                {event.year.replace('年', '')}
                <span className="timeline-year-small">年</span>
              </div>
            </div>
            <div className="timeline-content">
              <p>{event.content}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TimelinePage;
