import React from 'react';
import { useParams } from 'react-router-dom';
import '../styles/TimelinePage.css';

const timelineData = {
  1: [
    {
      year: "1969年",
      content: "一个老套的开场白：我在这一年降生到人世。等到幼儿园时，我们便开始不停搬家。我父亲是李维斯的推销员，我们一直在路上寻找牛仔裤的下一个大市场。我在15年里上了12所学校。"
    },
    {
      year: "1978—1979年",
      content: "创业#1：鸡蛋。我三年级时挨家挨户推销鸡蛋。我一直非常看好这项事业，因为这是一桩可靠的买卖。我们从一个农民那里低价买入鸡蛋，然后我和我的弟弟把这些鸡蛋推放到我们的蓝色推车里，每天早晨推着车到社区的街边售卖。这让我有了可以自由支配的零用钱，也让我第一次品尝到真正的自由的味道。\n\n如果我能坚持到底，谁知道我能把这个生意做多大。"
    },
    {
      year: "1980年",
      content: '找到了我一生所爱的事业。那是五年级的夏天。这真是一个发现你人生使命的好时机。我报了一门编程课。那时，"编程"就是拿着2号铅笔在小卡片上填充泡泡，然后在纸上画出结果。'
    }
  ]
};

function TimelinePage() {
  const { id } = useParams();
  const events = timelineData[id] || [];

  return (
    <div className="timeline-container">
      <div className="timeline">
        {events.map((event, index) => (
          <div key={index} className="timeline-item">
            <div className="timeline-year">{event.year}</div>
            <div className="timeline-content">
              {event.content.split('\n\n').map((paragraph, i) => (
                <p key={i}>{paragraph}</p>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TimelinePage;
