import React from 'react';
import { useParams, Link } from 'react-router-dom';
import styled from 'styled-components';
import { timelineData } from '../data/timelineData';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 40px 20px;
  background: white;
  min-height: 100vh;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 60px;
`;

const Title = styled.h1`
  font-size: 32px;
  font-weight: 500;
  color: #333;
  margin-bottom: 16px;
`;

const BackLink = styled(Link)`
  color: #666;
  text-decoration: none;
  font-size: 16px;
  &:hover {
    text-decoration: underline;
  }
`;

const Timeline = styled.div`
  position: relative;
  max-width: 800px;
  margin: 0 auto;
  padding: 20px 0;

  &::before {
    content: '';
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    width: 2px;
    height: 100%;
    background: #eee;
  }
`;

const TimelineItem = styled.div`
  display: flex;
  justify-content: center;
  align-items: flex-start;
  margin-bottom: 40px;
  position: relative;
`;

const YearLabel = styled(Link)`
  position: absolute;
  left: 0;
  width: 80px;
  text-align: right;
  padding-right: 30px;
  font-size: 14px;
  color: #666;
  text-decoration: none;

  &:hover {
    color: #333;
  }
`;

const AgeLabel = styled(Link)`
  position: absolute;
  right: 0;
  width: 80px;
  text-align: left;
  padding-left: 30px;
  font-size: 14px;
  color: #666;
  text-decoration: none;

  &:hover {
    color: #333;
  }
`;

const EventCard = styled.div`
  width: 400px;
  background: white;
  border-radius: 12px;
  padding: 24px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  margin: 0 60px;
`;

const EventTitle = styled.h3`
  font-size: 18px;
  font-weight: 500;
  color: #333;
  margin: 0 0 12px 0;
`;

const EventDescription = styled.p`
  font-size: 16px;
  color: #666;
  line-height: 1.5;
  margin: 0;
`;

const TimelinePage = () => {
  const { id } = useParams();
  const person = timelineData.find(p => p.id === parseInt(id));

  if (!person) {
    return <div>人物未找到</div>;
  }

  return (
    <Container>
      <Header>
        <Title>{person.name}的人生轨迹</Title>
        <BackLink to="/">返回首页</BackLink>
      </Header>
      <Timeline>
        {person.experiences.map((experience, index) => {
          const age = experience.year - person.birthYear;
          return (
            <TimelineItem key={index}>
              <YearLabel to={`/year/${experience.year}`}>
                {experience.year}
              </YearLabel>
              <EventCard>
                <EventTitle>{experience.title}</EventTitle>
                <EventDescription>{experience.description}</EventDescription>
              </EventCard>
              <AgeLabel to={`/age/${age}`}>
                {age}岁
              </AgeLabel>
            </TimelineItem>
          );
        })}
      </Timeline>
    </Container>
  );
};

export default TimelinePage;
