import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/YearEventsPage.css';

function YearEventsPage({ timelineData }) {
  const { year } = useParams();
  const navigate = useNavigate();
  const yearNumber = parseInt(year);

  // 获取指定年份的所有事件，并计算年龄
  const yearEvents = timelineData.flatMap(person => 
    person.experiences
      .filter(exp => exp.year === yearNumber)
      .map(exp => ({
        ...exp,
        personId: person.id,
        personName: person.name,
        age: yearNumber - person.birthYear
      }))
  );

  return (
    <div className="year-events-container">
      <div className="back-button" onClick={() => navigate(-1)}>
        返回上页
      </div>
      <div className="year-header">
        <h1>{yearNumber}<span className="year-label">年</span></h1>
        <p className="year-subtitle">这一年，他们都在做什么</p>
      </div>
      <div className="events-list">
        {yearEvents.map((event, index) => (
          <div key={index} className="event-item" onClick={() => navigate(`/timeline/${event.personId}`)}>
            <div className="event-person">
              <span className="person-name">{event.personName}</span>
              <span className="person-age">{event.age}岁</span>
            </div>
            <div className="event-content">
              <div className="event-title">{event.title}</div>
              <div className="event-description">{event.description}</div>
            </div>
          </div>
        ))}
        {yearEvents.length === 0 && (
          <div className="no-events">
            这一年暂无记录
          </div>
        )}
      </div>
    </div>
  );
}

export default YearEventsPage;
