import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';

const people = [
  {
    id: 1,
    name: '比尔·盖茨',
    title: '微软创始人',
    brief: '从哈佛辍学创业，改变了个人计算机的历史'
  },
  {
    id: 2,
    name: '马云',
    title: '阿里巴巴创始人',
    brief: '英语教师到互联网巨头掌门人'
  },
  {
    id: 3,
    name: '乔布斯',
    title: '苹果创始人',
    brief: '科技与人文的完美结合者'
  },
  {
    id: 4,
    name: '任正非',
    title: '华为创始人',
    brief: '从军人到科技企业家的传奇'
  },
  {
    id: 5,
    name: '柳传志',
    title: '联想集团创始人',
    brief: '中国改革开放后第一代企业家代表'
  },
  {
    id: 6,
    name: '雷军',
    title: '小米科技创始人',
    brief: '互联网思维改造传统制造业'
  },
  {
    id: 7,
    name: '李嘉诚',
    title: '长江实业创始人',
    brief: '白手起家的商业传奇'
  },
  {
    id: 8,
    name: '张朝阳',
    title: '搜狐创始人',
    brief: '中国互联��先驱者之一'
  },
  {
    id: 9,
    name: '马化腾',
    title: '腾讯创始人',
    brief: '从一款即时通讯软件到互联网帝国'
  },
  {
    id: 10,
    name: '王兴',
    title: '美团创始人',
    brief: '连续创业者，生活服务领域开拓者'
  }
];

function HomePage() {
  const navigate = useNavigate();

  return (
    <div className="home-container">
      <div className="home-header">
        <h1>岁月</h1>
        <div className="slogan">
          <p className="slogan-main">人生海海</p>
          <p className="slogan-sub">波涛浮沉</p>
        </div>
      </div>
      <div className="cards-grid">
        {people.map((person) => (
          <div 
            key={person.id}
            className="person-card"
            onClick={() => navigate(`/timeline/${person.id}`)}
          >
            <div className="card-image">
              <div className="placeholder-image">
                <span>{person.name[0]}</span>
              </div>
            </div>
            <div className="card-content">
              <h2>{person.name}</h2>
              <h3>{person.title}</h3>
              <p>{person.brief}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
