import React from 'react';
import { useParams, Link } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 40px 20px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
`;

const Header = styled.div`
  margin-bottom: 40px;
  text-align: center;
`;

const Title = styled.h1`
  font-size: 32px;
  font-weight: 500;
  color: #333;
  margin-bottom: 10px;
`;

const BackLink = styled(Link)`
  color: #666;
  text-decoration: none;
  font-size: 16px;
  &:hover {
    text-decoration: underline;
  }
`;

const EventList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const EventCard = styled.div`
  background: #fff;
  border-radius: 12px;
  padding: 24px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
`;

const EventDate = styled.div`
  font-size: 14px;
  color: #666;
  margin-bottom: 8px;
`;

const EventTitle = styled.h3`
  font-size: 18px;
  font-weight: 500;
  color: #333;
  margin: 0 0 12px 0;
`;

const EventDescription = styled.p`
  font-size: 16px;
  color: #666;
  line-height: 1.5;
  margin: 0;
`;

const AgeEventsPage = ({ timelineData }) => {
  const { age } = useParams();
  const ageNumber = parseInt(age);

  const getEventsForAge = (data, targetAge) => {
    return data.flatMap(person => 
      person.experiences.filter(exp => {
        const eventAge = exp.year - person.birthYear;
        return eventAge === targetAge;
      }).map(exp => ({
        ...exp,
        personName: person.name,
        age: targetAge
      }))
    );
  };

  const ageEvents = getEventsForAge(timelineData, ageNumber);

  return (
    <Container>
      <Header>
        <Title>{ageNumber} 岁的经历</Title>
        <BackLink to="/">返回首页</BackLink>
      </Header>
      <EventList>
        {ageEvents.map((event, index) => (
          <EventCard key={index}>
            <EventDate>{event.year}年 - {event.personName}</EventDate>
            <EventTitle>{event.title}</EventTitle>
            <EventDescription>{event.description}</EventDescription>
          </EventCard>
        ))}
      </EventList>
    </Container>
  );
};

export default AgeEventsPage;
